function createStore(reducer,state = {},rewriteCreateStore){
    if(rewriteCreateStore){
        createStore = rewriteCreateStore(createStore);
        return createStore(reducer,state)
    }
    state = state;
    listeners = [];    // 
    function subscribe(listener){
        if(typeof listener === 'function'){
            listeners.push(listener)
        }else{
            throw new Error (`${listener} is not function`)
        }
        return function(listener){      //    
            const index = listeners.indexOf(listener);
            if(index > -1){
                listeners.splice(index,1)
            }
        }
    }
    function getState(){
        return state
    }
    function dispatch(action){
        state = reducer(state,action);
        listeners.forEach((listener) => {
            listener()
        })
    }
    dispatch({type:Symbol()})   //state getState 
    return {
        getState,
        subscribe,
        dispatch
    }
}

function combindReducers(reducers){
    const newState = {};
    return function(state,action){
        Object.keys(reducers).forEach(key => {
            const reducer = reducers[key];
            const prevState = state[key];
            const nextState = reducer(prevState,action)
            newState[key] = nextState
        })
        return newState
    }
}

function counterReducer (state = { count:0 },action){
    switch(action.type){
        case 'INCREMENT':
            return{
                ...state,
                count:state.count + 1,
            }
        case 'DECREMENT':
            return{
                ...state,
                count:state.count - 1,
            }
        default:
            return state        
    }
}

function infoReducer (state = {name:'',description:''},action){
    switch(action.type){
        case 'SET_NAME':
            return {
                ...state,
                name:action.payload.name
            }
        case 'SET_DESCRIPTION':
            return {
                ...state,
                description:action.payload.description
            }  
        default:
            return state      
    }
}

const reducer = combindReducers({
    counter:counterReducer,
    info:infoReducer
})

function compose(funcs){
    if(funcs.length === 0)return  (...arg) => arg;
    if(funcs.length === 1)return funcs[0];
    return funcs.reduce((acc,cur) => (...arg) => acc(cur(...arg)))
}

function applyMiddleware(...middlewares){
    return function(createStore){
        return function(reducer,state){
            const store = createStore(reducer,state);
            const chain = middlewares.map(middleware => middleware(store));
            const dispatch = compose(chain)(store.dispatch)
            return {
                ...store,
                dispatch
            }
        }
    }
}

function expectionMiddleware(store){
    return function (next){
        return function(action){
            try {
                console.log('caghterror')
                next(action)
            }catch(e){
                console.log(e)
            }   
        }
    }
}

function loggerMiddleware(store){
    return function(next){
        return function (action){
            console.log('logger')
            next(action)
        }
    }
}


function timeMiddleware(store){
    return function(next){
        return function (action){
            console.log(new Date().getTime(),'')
            next(action)
        }
    }
}

const rewriteCreateStore = applyMiddleware(expectionMiddleware,timeMiddleware,loggerMiddleware)

const store = createStore(reducer,{},rewriteCreateStore)

store.subscribe(()=>{
    console.log(store.getState(),'')
})

function increment (payload){
    return {
        type:'INCREMENT'
    }
}

function setName (payload){
    return {
        type:'SET_NAME',
        payload
    }
}


function bindActionCreators(actions,dispatch){
    const newActions = {}
    Object.keys(actions).forEach(key => {
        newActions[key] = (...arg) => {
            dispatch(actions[key].apply(this,...arg))
        }
    })
    return newActions
}

const actions = bindActionCreators({
    increment,setName
},store.dispatch)

store.dispatch({type:'INCREMENT'})
actions.setName({name:''})

